        },
      ];

      setData(res.Main)

  }


  return (
    <Box m="20px">
      <Header title="Pie Chart" subtitle="Simple Pie Chart" />
      <Box height="75vh">
        <PieChart inputData={solarData} />
      </Box>
    </Box>
  );
};

export default Pie;
