import { Box, Button, IconButton, Typography, useTheme } from "@mui/material";
import Header from "../../components/Header";
import BarChart from "../../components/BarChart";
import { actualBarData as datamock } from "../../data/mockData";
import { useEffect, useState } from "react";
import { tokens } from "../../theme";
import StatBox from "../../components/StatBox";
import QueryBuilderIcon from '@mui/icons-material/QueryBuilder';

const Bar = (props) => {

  let [data, setData] = useState([])
useEffect(()=>{

 getData();
},[])
if(props.city === ""){
  props.city = "mumbai"
 }
let getData = async () =>{

    let response = await fetch('http://127.0.0.1:8000/api/city_bar/'+props.city)
    let res = await response.json()
    //console.log(res[0]["Maximum Temperature"])
    console.log(res.Main)
    setData(res.Main)

}

const theme = useTheme();
  const colors = tokens(theme.palette.mode);
  const timecards = data.length===0?'': data["Time"].map(item=>{
    //let increase = parseFloat(data[item]["expected"])===0?0:((parseFloat(data[item]["current"])-parseFloat(data[item]["expected"]))/parseFloat(data[item]["expected"]))*100

    return(
       <Box
       gridColumn={"span 6"}
       backgroundColor={colors.primary[400]}
       display="flex"
       alignItems="center"
       justifyContent="center"
     >
       <StatBox
         title= {item.Time}
         subtitle={item.Date}
         progress={item.Name}
         trending = {""}
         increase={""}
         icon={
          <QueryBuilderIcon 
          sx={{ color: colors.greenAccent[600], fontSize: "26px" }}
          />
         }
       />
     </Box>
    )

  })

  return (
   <>
   {timecards}
    <Box m="20px" sx={{  backgroundColor: colors.primary[400] }}>
      <Header title="Power Consumption vs Generation" subtitle="" />
      <Box height="100vh">
        <BarChart Inputdata={data} keys={['Wind','Solar']} index={'Source'} />
      </Box>
    </Box>
    </>
  );
};

export default Bar;
